Alpha.script:
'use strict';

function clamp(value, min = 0.0, max = 1.0) {
	return Math.min(Math.max(value, min), max);
}

let transitionTime = 0.4;


/**
 * @param {Number} value - for property 'Alpha'
 * @return {Number} - update current property value
 */
export function update(value) {
	if (shared.Demo) {
		// ~ 4 second cycle for thumbnail
		value = 1.0 + 2.0 * Math.sin(engine.runtime * Math.PI / 2.4);
	}
	else {
		if (shared.hoverPainting) {
			value += engine.frametime * 1.0 / transitionTime;
		}
		else {
			value -= engine.frametime * 1.0 / transitionTime;
		}
	}
	return clamp(value);
}
