'use strict';

export function init() {
	shared.normalColor = new Vec3(1, .5, .1);
	shared.ghostColor = new Vec3(.1, 1, .5);

	for (let i = 1; i <= 6; ++i) {
		let emitter = thisScene.getLayer(`candle_flame_${i}`);
		emitter.instance.lifetime = 0.7 + Math.random() * 0.6;
	}
}

'use strict';

var initialPos;
var orbitPos;
var smoothness = 0.1;
var panAmount = new Vec2(-1.0, 0.5);
var rightVector;
var movementScale = 1.0;

export function init() {
	let cameraTransforms = thisScene.getCameraTransforms();
	initialPos = thisScene.getLayer("cameraEye").origin;
	orbitPos = thisScene.getLayer("cameraOrbit").origin;
	rightVector = initialPos.subtract(orbitPos).cross(cameraTransforms.up).normalize();
	thisScene.setCameraTransforms(cameraTransforms);
}

export function update() {
	let cameraTransforms = thisScene.getCameraTransforms();
	// Normalized cursor distance from center of screen
	let screenPosition = input.cursorScreenPosition.divide(engine.screenResolution).subtract(0.5, 0.5);
	// Where the camera wants to go
	let targetPos = initialPos.add(rightVector.multiply(panAmount.x * screenPosition.x * movementScale));
	targetPos = targetPos.add(cameraTransforms.up.multiply(panAmount.y * screenPosition.y * movementScale));
	// Vector from current position to the target position
	let toTarget = targetPos.subtract(cameraTransforms.eye);
	if (smoothness > 0.0001) {
		cameraTransforms.eye = cameraTransforms.eye.add(toTarget.multiply(1.0 - Math.pow(0.5, engine.frametime / smoothness)));
	}
	else {
		cameraTransforms.eye = targetPos;
	}
	cameraTransforms.center = orbitPos;
	thisScene.setCameraTransforms(cameraTransforms)
}

/**
 * @param {Object} changedUserProperties - only includes user properties that were recently changed!
 */
export function applyUserProperties(changedUserProperties) {
	if (changedUserProperties.cameramovementscale !== undefined) {
		movementScale = changedUserProperties.cameramovementscale;
	}
	if (changedUserProperties.cameramovementsmoothness !== undefined) {
		smoothness = changedUserProperties.cameramovementsmoothness;
	}
}

'use strict';

shared.hoverPainting = false;

/**
 * @param {CursorEvent} event
 */
export function cursorEnter(event) {
	shared.hoverPainting = true;
}

/**
 * @param {CursorEvent} event
 */
export function cursorLeave(event) {
	shared.hoverPainting = false;
}

intensity.script:
'use strict';

let frequency1 = 10 + Math.random() * 10;
let frequency2 = 25 + Math.random() * 10;
let frequency3 = 40 + Math.random() * 20;

/**
 * @param {Number} value - for property 'intensity'
 * @return {Number} - update current property value
 */
export function update(value) {
	return 1 + 
		0.07 * Math.sin(engine.runtime * frequency1) + 
		0.05 * Math.sin(engine.runtime * frequency2) + 
		0.03 * Math.sin(engine.runtime * frequency3);
}

origin.script:
'use strict';

/**
 * @param {Vec3} value - for property 'origin'
 * @return {Vec3} - update current property value
 */
export function update(value) {
	if (shared.hoverCandle1) {
		return thisScene.getLayer("candle_flame_1").origin;
	}
	else if (shared.hoverCandle2) {
		return thisScene.getLayer("candle_flame_2").origin;
	}
	else if (shared.hoverCandle3) {
		return thisScene.getLayer("candle_flame_3").origin;
	}
}

